import React from 'react'
import './Log.css'
import { useState } from 'react';
import { useNavigate } from 'react-router-dom'
// import { useAuthContext } from '../../AuthContext';
import axios from 'axios';

export default function Log() {
  const navigate = useNavigate();
  const [email, changeEmail] = useState("");
  const [password, changePassword] = useState("");
  const [errorMessage, changeErrorMessage] = useState('');
  const [user, updateUser] = useState("");


  const submitChange =  async() => {
    console.log(email, password);
    await axios
      .post('login', { email, password })
      .then((response) => {
        console.log(response)
        updateUser(response.data.payload.user)
        navigate('/');
        console.log(JSON.stringify(response));
      })
      .catch((error) => {
        changeErrorMessage('email or password is not correct', error);
      });
  };
  return (
    <div className="frm">
      <form className="login">
        <span className='frmhead'>Log In</span>
        <label htmlFor="email">Username: </label>
        <input
          type="email"
          name="username"
          placeholder="Username"
          id="email"
          onChange={(e) => changeEmail(e.target.value)}
        />
        <label htmlFor="pwd">Password: </label>
        <input
          type="password"
          name="password"
          placeholder="Password"
          id="pwd"
          onChange={(e) => changePassword(e.target.value)}
        />
        <input
          type="button"
          value="Login"
          id="login"
          onClick={submitChange}
        />
        <input
          type="button"
          value="Register New"
          id="register"
          onClick={() => navigate("/Register")}
        />
      </form>
      {errorMessage && <p style={{background: "red", padding: "10px", color: "#fff", maxWidth:"500px"}}>{errorMessage}</p>}
    </div>
  );
}
