import React, { useEffect,useState } from 'react'
import { useParams} from 'react-router-dom'
import "./Search.css"
import axios from "axios"

const Search = () => {
  const params=useParams();
  const userId=params.id;
  // console.log(userId)

  const [list,setList]=useState([])
  const [svalue,setSvalue]=useState("")
  const [key,setKey]=useState("")

  const reqDetail={
    from:userId,
    to:key
  }

  const sendReq = async () => {
    await axios.post("http://localhost:4000/request", reqDetail)
    .then((res)=>{
      console.log(res)
      alert("Friend Request Send")
    })
    .catch((err)=>{
      console.log(err)
      alert("Not able to send request")
    })
  }
  const searchResult = async () => {
    await axios
    .get("http://localhost:4000/user/"+svalue)
    .then((res) => {
      setList(res.data);
    })
    .catch((err) => console.log(err));
}

useEffect( () => {
  searchResult()
}, [])

  return (
    <div className='usersearch'>
      <input className='searchbar' onChange={(e)=>{setSvalue(e.target.value)}}></input>
      <button className='searchbtn' onClick={searchResult}>Search</button>
      {list.map(c=>{
            const {id,name}=c;
            return(
              <>
         <div className='searchresults' key={id}>
           <div className='fullname'>{name}</div>
           <button className='sendreq' onClick={()=>{sendReq}}>Send Request</button>
         </div>
              </>
            )
          })}
    </div>
  )
}

export default Search
