import React from 'react'
import './Register.css'
import { useState } from 'react';
import { useNavigate } from 'react-router-dom'
// import { useAuthContext } from '../../AuthContext';
import axiosInstance from 'axios';

export default function Register() {
  const navigate = useNavigate();
  const [email, changeEmail] = useState("");
  const [password, changePassword] = useState("");
  const [errorMessage, changeErrorMessage] = useState('');
  // const {user, updateUser} = useAuthContext()


  const submitChange =  (e) => {
    console.log(email, password);
    e.preventDefault();
    // axiosInstance
    //   .post('login', { email, password })
    //   .then((response) => {
    //     console.log(response)
    //     updateUser(response.data.payload.user)
    //     navigate('/');
    //     console.log(JSON.stringify(response));
    //   })
    //   .catch((error) => {
    //     changeErrorMessage('email or password is not correct', error);
    //   });
  };
  return (
    <div className="frm">
      <form className="register" method="post" action="">
        <span className='frmhead'>Register</span>
        <label htmlFor="email">Username </label>
        <input
          type="email"
          name="username"
          placeholder="Username"
          id="email"
          onChange={(e) => changeEmail(e.target.value)}
        />
        <label htmlFor="pwd">Password</label>
        <input
          type="password"
          name="password"
          placeholder="Password"
          id="pwd"
          onChange={(e) => changePassword(e.target.value)}
        />


        <label htmlFor="pwd">First Name</label>
        <input
          type="password"
          name="password"
          placeholder="Password"
          id="pwd"
          onChange={(e) => changePassword(e.target.value)}
        />
        <label htmlFor="pwd">First Name</label>
        <input
          type="password"
          name="password"
          placeholder="Password"
          id="pwd"
          onChange={(e) => changePassword(e.target.value)}
        />



        <br />
        <input
          type="button"
          value="Register"
          id="register"
          onClick={submitChange}
        />
        &nbsp;&nbsp;
        <input
          type="button"
          value="Go to Login"
          id="login"
          onClick={() => navigate("/")}
        />
        {errorMessage && <p style={{background: "red", padding: "10px", color: "#fff", maxWidth:"500px"}}>{errorMessage}</p>}
      </form>
    </div>
  );
}
