import React, { useEffect,useState } from 'react'
import "./Home.css"
import {Routes,Route,useParams} from "react-router-dom"

const Home = () => {
  const {socket}=useOutletContext();

  const params=useParams();
  const [msg,setMsg]=useState("")
  const [chat,setChat]=useState([])

  useEffect(()=>{
    if(socket){

      socket.on("receiveMsg",(rarg)=>{
        console.log(rarg)
        setChat(chat => [...chat,{message:rarg,received:true}])
        console.log(chat)
      })

    }
    },[socket])

  useEffect(()=>{
    console.log(params.roomId)
    socket.emit("join-room",{roomId: params.roomId})
  },[params])




  const handleSubmit=()=>{
    socket.emit("sendMsg",msg)
    setChat(chat => [...chat,{message:msg,received:false}])
    setMsg("")
  }


  return (
    <>
      <div className='container'>
        <div className='child'>
          <span className='RoomId'>{params.roomId}</span>
          {
            chat.map(n=>{
              return <div key={n.message} className={n.received ? "second":"first"}>{n.message}</div>
            })
          }
        </div>
        <div className='ip'>
          <input className='msg' placeholder="Type your message" value={msg} onChange={(e)=>setMsg(e.target.value)}></input>
          <span className='send' onClick={handleSubmit}>Send</span>
        </div>
      </div>
    </>
  )
}

export default Home
