import React from 'react'
import './Register.css'
import { useState } from 'react';
import { useNavigate } from 'react-router-dom'
// import { useAuthContext } from '../../AuthContext';
import axiosInstance from 'axios';

export default function Register() {
  const navigate = useNavigate();
  const [errorMessage, changeErrorMessage] = useState('');
  // const {user, updateUser} = useAuthContext()

  const [state,setState]=useState({
    username:"",
    password:"",
    firstname:"",
    lastname:"",
    country:"",
    gender:"",
    dob:""
  })

  const changeHandler = (event) => {
    const {name, value} = event.target;
    setState({
      ...state,
      [name]: value
    })
  } 

  const addHandler = async () => {
    try {
      const data =await axios.post("http://localhost:3000/user/register", {...state})
      console.log(data)

      navigate('/');
    } catch(error){
      console.log(error)
      changeErrorMessage(error.error)
    }
  }

  return (
    <div className="frm">
      <form className="register" method="post" action="">
        <span className='frmhead'>Register</span>
        <label htmlFor="email">Username </label>
        <input
          type="email"
          name="username"
          placeholder="Username"
          id="email"
          onChange={changeHandler}
        />
        <label htmlFor="pwd">Password</label>
        <input
          type="password"
          name="password"
          placeholder="Password"
          id="pwd"
          onChange={changeHandler}
        />


        <label htmlFor="fname">First Name</label>
        <input
          type="text"
          name="firstname"
          placeholder="First Name"
          id="fname"
          onChange={changeHandler}
        />
        <label htmlFor="lname">Last Name</label>
        <input
          type="text"
          name="lastname"
          placeholder="Last Name"
          id="lname"
          onChange={changeHandler}
        />

        <label htmlFor="gender">Gender</label>
        <input
          type="text"
          name="gender"
          placeholder="Gender"
          id="gender"
          onChange={changeHandler}
        />
        <label htmlFor="dob">DOB</label>
        <input
          type="date"
          name="dob"
          placeholder="Date of Birth"
          id="dob"
          onChange={changeHandler}
        />
        <label htmlFor="country">Country</label>
        <input
          type="text"
          name="country"
          placeholder="Country"
          id="country"
          onChange={changeHandler}
        />


        <br />
        <input
          type="button"
          value="Register"
          id="register"
          onClick={addHandler}
        />
        &nbsp;&nbsp;
        <input
          type="button"
          value="Go to Login"
          id="login"
          onClick={() => navigate("/")}
        />
        {errorMessage && <p style={{background: "red", padding: "10px", color: "#fff", maxWidth:"500px"}}>{errorMessage}</p>}
      </form>
    </div>
  );
}
