import React, { useEffect,useState } from 'react'
import "./Home.css"
import {io} from "socket.io-client"

const Home = () => {

  const [msg,setMsg]=useState("")
  const [chat,setChat]=useState([])
  const [socket,setSocket]=useState(null)

  useEffect(()=>{
  setSocket(io("http://localhost:3001"))
  },[])


  useEffect(()=>{
  if(socket){

    socket.on("receiveMsg",(rarg)=>{
      console.log(rarg)
      setChat(chat => [...chat,{message:rarg,received:true}])
      console.log(chat)
    })

  }
  },[socket])


  const handleSubmit=()=>{
    socket.emit("sendMsg",msg)
    setChat(chat => [...chat,{message:msg,received:false}])
    setMsg("")
  }


  return (
    <div className='bg'>
      <div className='container'>
        <div className='child'>
          {
            chat.map(n=>{
              return <div key={n.message} className={n.re}>{n.message}</div>
            })
          }
          {/* <div className='second'>
            a
          </div> */}

        </div>
        <div className='ip'>
          <input className='msg' placeholder="Type your message" value={msg} onChange={(e)=>setMsg(e.target.value)}></input>
          <span className='send' onClick={handleSubmit}>Send</span>
        </div>
      </div>
    </div>
  )
}

export default Home
