import axios from 'axios'
import React, { useEffect } from 'react'
import { useState,use } from 'react'

const Notification = () => {

  const params=useParams();
  const userId=params.id;

  const [list,setList]=useState([])

  const getNotification=async()=>{
    await axios.get("http://localhost:4000/request/"+userId,{"headers":{
      authorization:`Bearer ${localStorage.getItem('LoginToken')}`
    }})
    .then((res)=>{
      setList(res.data)
    })
    .catch((err)=>{
      console.log(err)
      navigate("/")
    })
  }



  useEffect(()=>{
    getNotification()
  },[] )

  return (
    <div className='usernoti'>
      {list.map(c=>{
            return(
              <>
         <div className='pendingreq'>
           <div className='fromname'></div>
           <button className='accept'>Send Request</button>
           <button className='reject'>Search</button>
         </div>
              </>
            )
          })}
    </div>
  )
}

export default Notification
